ems_template.parameter_schema:
module common-password-config {
  namespace "urn:ietf:params:xml:ns:yang:common-password-config";
  prefix "common-password-config";

  revision 2017-08-24 {
    description
      "Initial revision";
  }

  container configuration {
    leaf password {
      type string;
      mandatory true;
      description
        "Device root password field";
    }
  }
}

ems_template.config_in_text:
{% if pre_config.password is defined and post_config == {} %}
set system root-authentication encrypted-password {{pre_config.password}}
{% endif %}
{% if post_config != {} %}
set system root-authentication encrypted-password {{post_config.password}}
{% endif %}

ems_template.view_def:
{
  "viewdef": {
    "version": "2.0", 
    "path": "$", 
    "viewdefs": [
      {
        "type": "panel", 
        "children": [
          {
            "children": [
              {
                "label": "Password", 
                "type": "PASSWORD_STRENGTH", 
                "required": true, 
                "id": "password", 
                "path": "password", 
                "hidden": false, 
                "scope": "entity"
              }
            ], 
            "path": "."
          }
        ], 
        "id": "entry", 
        "path": "configuration", 
        "post_process": {
          "type": "function", 
          "function": "var d = arguments[0]; var payload = {input: {plain_text: d.configuration.password}}; return this.sendRequest('/ems-central/crpyt-text-to-hash', 'POST', payload).then(function(r){d.configuration.password=r.output.result; return d;})"
        }, 
        "properties": {
          "size": "xlarge"
        }, 
        "readonly": false
      }
    ]
  }
}

