ems_template.parameter_schema:
module nfx-sdwan-jcp-mgmnt {
  namespace "urn:ietf:params:xml:ns:yang:nfx-sdwan-jcp-mgmnt";
  prefix nfx-sdwan-jcp-mgmnt;

  revision 2021-05-03 {
    description
      "Initial revision";
  }

  container configuration {
    leaf ntp_server {
      type string;
      mandatory true;
      description
        "";
    }
    container post_config {
      description
        "";
      leaf USE_SINGLE_SSH_TO_NFX {
        type string;
        description
          "";
      }
      container ntp {
        description
          "";
        leaf-list NTP_servers {
          type string;
          min-elements 1;
          description
            "";
        }
        leaf NTP_Source_Address {
          type string;
          mandatory true;
          description
            "";
        }
        leaf NTP_Boot_Server {
          type string;
          mandatory true;
          description
            "";
        }
        leaf enable_service {
          type string;
          description
            "";
        }
      }
      container snmp {
        description
          "";
        list snmpv3_users {
          key "encryption_mode";
          description
            "";
          leaf encryption_mode {
            type string;
            mandatory true;
            description
              "";
          }
          leaf authentication_mode {
            type string;
            mandatory true;
            description
              "";
          }
          leaf auth_password {
            type string;
            mandatory true;
            description
              "";
          }
          leaf enc_password {
            type string;
            mandatory true;
            description
              "";
          }
          leaf snmp_user {
            type string;
            mandatory true;
            description
              "";
          }
        }
        leaf enabled {
          type string;
          description
            "";
        }
        list trap_receivers {
          key "trap_dest";
          description
            "";
          leaf trap_dest {
            type string;
            mandatory true;
            description
              "";
          }
          leaf snmp_host_prefix {
            type string;
            mandatory true;
            description
              "";
          }
          leaf snmp_user_trap {
            type string;
            mandatory true;
            description
              "";
          }
        }
        leaf traps_enabled {
          type string;
          description
            "";
        }
      }
      container syslog {
        description
          "";
        leaf enabled {
          type string;
          description
            "";
        }
        list syslog_collectors {
          key "host";
          description
            "";
          leaf host {
            type string;
            mandatory true;
            description
              "";
          }
          leaf level {
            type string;
            mandatory true;
            description
              "";
          }
          leaf facility {
            type string;
            mandatory true;
            description
              "";
          }
        }
      }
      container tacacs {
        description
          "";
        leaf tacacs_server_ip {
          type string;
          mandatory true;
          description
            "";
        }
        leaf tacacs_server_password {
          type string;
          mandatory true;
          description
            "";
        }
        leaf enabled {
          type string;
          description
            "";
        }
        leaf tacacs_server_port {
          type string;
          mandatory true;
          description
            "";
        }
        leaf local_password {
          type string;
          mandatory true;
          description
            "";
        }
      }
      container alarms {
        description
          "";
        leaf mgmt_link_ignore {
          type string;
          description
            "";
        }
      }
      leaf local_address {
        type string;
        mandatory true;
        description
          "";
      }
    }
  }
}

ems_template.config_in_text:
delete groups nfx-sdwan-jcp-mgmnt
delete apply-groups nfx-sdwan-jcp-mgmnt

{% if post_config.USE_SINGLE_SSH_TO_NFX is defined and post_config.USE_SINGLE_SSH_TO_NFX == True%}

     {% set local_loopback_address = "100.123.250.3" %}

{% else %}

     {% set local_loopback_address =  post_config.local_address %}

{% endif %}

{# SNMP Configuration Section #}

{% if post_config.snmp.enabled is defined and post_config.snmp.enabled and
      post_config.snmp.snmpv3_users is defined and post_config.snmp.snmpv3_users|length > 0 %}

{% for item in post_config.snmp.snmpv3_users %}

{% if item.authentication_mode != "None" %}
set groups nfx-sdwan-jcp-mgmnt snmp v3 usm local-engine user {{ item.snmp_user }} authentication-{{ item.authentication_mode|lower }} authentication-password {{ item.auth_password }}

    {% if item.encryption_mode != "None"%}
    set groups nfx-sdwan-jcp-mgmnt snmp v3 usm local-engine user {{ item.snmp_user }} privacy-{{ item.encryption_mode|lower }} privacy-password {{ item.enc_password }}
    {% else %}
    set groups nfx-sdwan-jcp-mgmnt snmp v3 usm local-engine user {{ item.snmp_user }} privacy-none
    {% endif %}

{% else%}
set groups nfx-sdwan-jcp-mgmnt snmp v3 usm local-engine user {{ item.snmp_user }} authentication-none
set groups nfx-sdwan-jcp-mgmnt snmp v3 usm local-engine user {{ item.snmp_user }} privacy-none
{% endif %}


set groups nfx-sdwan-jcp-mgmnt snmp v3 vacm security-to-group security-model usm security-name {{ item.snmp_user }} group grpjnpr

{% endfor %}




set groups nfx-sdwan-jcp-mgmnt snmp v3 vacm access group grpjnpr default-context-prefix security-model any security-level authentication read-view all
set groups nfx-sdwan-jcp-mgmnt snmp v3 vacm access group grpjnpr default-context-prefix security-model any security-level authentication write-view all


{% if post_config.snmp.traps_enabled is defined and post_config.snmp.traps_enabled and
      post_config.snmp.trap_receivers is defined and post_config.snmp.trap_receivers|length > 0 %}

{% for item in post_config.snmp.trap_receivers %}

set groups nfx-sdwan-jcp-mgmnt snmp v3 target-address receiver{{loop.index}} address {{ item.trap_dest }}
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-address receiver{{loop.index}} port 162
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-address receiver{{loop.index}} tag-list net-snmp
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-address receiver{{loop.index}} target-parameters tp{{loop.index}}
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-parameters tp{{loop.index}} parameters message-processing-model v3
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-parameters tp{{loop.index}} parameters security-model usm
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-parameters tp{{loop.index}} parameters security-level authentication
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-parameters tp{{loop.index}} parameters security-name {{ item.snmp_user_trap }}
set groups nfx-sdwan-jcp-mgmnt snmp v3 target-parameters tp{{loop.index}} notify-filter nf1
set groups nfx-sdwan-jcp-mgmnt routing-options static route {{item.snmp_host_prefix}} next-hop {{local_loopback_address}}

{% endfor %}

{% endif %}

set groups nfx-sdwan-jcp-mgmnt snmp v3 notify n1 type trap
set groups nfx-sdwan-jcp-mgmnt snmp v3 notify n1 tag net-snmp
set groups nfx-sdwan-jcp-mgmnt snmp v3 notify-filter nf1 oid .1 include
set groups nfx-sdwan-jcp-mgmnt snmp view all oid .1 include

{#set groups nfx-sdwan-jcp-mgmnt security zones security-zone untrust host-inbound-traffic system-services snmp#}

set apply-groups nfx-sdwan-jcp-mgmnt

{% endif %}



{# Syslog Configuration Section #}


{% if post_config.syslog.enabled is defined and post_config.syslog.enabled and
      post_config.syslog.syslog_collectors is defined and post_config.syslog.syslog_collectors|length > 0 %}

    {% for entry in post_config.syslog.syslog_collectors %}
set groups nfx-sdwan-jcp-mgmnt system syslog host {{ entry.host }} {{entry.facility}} {{entry.level}}
set groups nfx-sdwan-jcp-mgmnt system syslog host {{ entry.host }} dfc none
set groups nfx-sdwan-jcp-mgmnt system syslog host {{ entry.host }} external none
set groups nfx-sdwan-jcp-mgmnt system syslog host {{ entry.host }} change-log none
set groups nfx-sdwan-jcp-mgmnt system syslog host {{ entry.host }} explicit-priority
set groups nfx-sdwan-jcp-mgmnt routing-options static route {{ entry.host }} next-hop {{local_loopback_address}}
    {% endfor %}

set groups nfx-sdwan-jcp-mgmnt system syslog archive size 2m
set groups nfx-sdwan-jcp-mgmnt system syslog archive files 5
set groups nfx-sdwan-jcp-mgmnt system syslog archive world-readable
set groups nfx-sdwan-jcp-mgmnt system syslog user * any emergency
set groups nfx-sdwan-jcp-mgmnt system syslog file interactive-commands interactive-commands any
set groups nfx-sdwan-jcp-mgmnt system syslog file interactive-commands archive size 10m
set groups nfx-sdwan-jcp-mgmnt system syslog file interactive-commands archive files 10
set groups nfx-sdwan-jcp-mgmnt system syslog file firewall any info
set groups nfx-sdwan-jcp-mgmnt system syslog file firewall match RT_FLOW_SESSION_DENY
set groups nfx-sdwan-jcp-mgmnt system syslog file firewall archive size 10m
set groups nfx-sdwan-jcp-mgmnt system syslog file firewall archive files 10
set groups nfx-sdwan-jcp-mgmnt system syslog file traffic-log any any
set groups nfx-sdwan-jcp-mgmnt system syslog file traffic-log match "RT_FLOW_SESSION_CREATE|RT_FLOW_SESSION_CLOSE"
set groups nfx-sdwan-jcp-mgmnt system syslog file traffic-log archive size 10m
set groups nfx-sdwan-jcp-mgmnt system syslog file traffic-log archive files 10
set groups nfx-sdwan-jcp-mgmnt system syslog file messages any info
set groups nfx-sdwan-jcp-mgmnt system syslog file messages authorization none
set groups nfx-sdwan-jcp-mgmnt system syslog file messages firewall none
set groups nfx-sdwan-jcp-mgmnt system syslog file messages change-log notice
set groups nfx-sdwan-jcp-mgmnt system syslog file messages interactive-commands any
set groups nfx-sdwan-jcp-mgmnt system syslog file messages explicit-priority
set groups nfx-sdwan-jcp-mgmnt system syslog console any critical
set groups nfx-sdwan-jcp-mgmnt system syslog time-format year
set groups nfx-sdwan-jcp-mgmnt system syslog time-format millisecond


set apply-groups nfx-sdwan-jcp-mgmnt
{% endif%}


{# TACACS Configuration Section #}


{% if post_config.tacacs.enabled is defined and post_config.tacacs.enabled and
      post_config.tacacs.tacacs_server_ip is defined and post_config.tacacs.tacacs_server_password is defined %}

set groups nfx-sdwan-jcp-mgmnt system authentication-order tacplus
set groups nfx-sdwan-jcp-mgmnt system authentication-order password

    {% if  post_config.tacacs.tacacs_server_port is defined %}
set groups nfx-sdwan-jcp-mgmnt system tacplus-server {{ post_config.tacacs.tacacs_server_ip }} port {{ post_config.tacacs.tacacs_server_port }}
    {% else %}
set groups nfx-sdwan-jcp-mgmnt system tacplus-server {{ post_config.tacacs.tacacs_server_ip }} port 49
    {% endif %}
set groups nfx-sdwan-jcp-mgmnt system tacplus-server {{ post_config.tacacs.tacacs_server_ip }} secret {{ post_config.tacacs.tacacs_server_password }}
set groups nfx-sdwan-jcp-mgmnt system tacplus-server {{ post_config.tacacs.tacacs_server_ip }} timeout 3

set groups nfx-sdwan-jcp-mgmnt system accounting events login
set groups nfx-sdwan-jcp-mgmnt system accounting events change-log
set groups nfx-sdwan-jcp-mgmnt system accounting events interactive-commands

    {% if  post_config.tacacs.tacacs_server_port is defined %}
set groups nfx-sdwan-jcp-mgmnt system accounting destination tacplus server {{ post_config.tacacs.tacacs_server_ip }} port {{ post_config.tacacs.tacacs_server_port }}
    {% else %}
set groups nfx-sdwan-jcp-mgmnt system accounting destination tacplus server {{ post_config.tacacs.tacacs_server_ip }} port 49
    {% endif %}
set groups nfx-sdwan-jcp-mgmnt system accounting destination tacplus server {{ post_config.tacacs.tacacs_server_ip }} secret {{ post_config.tacacs.tacacs_server_password }}
set groups nfx-sdwan-jcp-mgmnt system accounting destination tacplus server {{ post_config.tacacs.tacacs_server_ip }} timeout 3
set groups nfx-sdwan-jcp-mgmnt routing-options static route {{ post_config.tacacs.tacacs_server_ip }} next-hop {{local_loopback_address}}

set groups nfx-sdwan-jcp-mgmnt system login class readonly idle-timeout 10
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions admin
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions firewall
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions interface
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions network
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions routing
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions security
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions snmp
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions system
set groups nfx-sdwan-jcp-mgmnt system login class readonly permissions view
set groups nfx-sdwan-jcp-mgmnt system login class readonly deny-commands "(start shell)"
set groups nfx-sdwan-jcp-mgmnt system login class readwrite idle-timeout 10
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions access
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions access-control
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions admin
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions clear
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions configure
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions control
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions firewall
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions firewall-control
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions floppy
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions interface
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions interface-control
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions maintenance
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions network
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions reset
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions rollback
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions routing
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions routing-control
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions secret
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions security
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions system
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions system-control
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions trace
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions trace-control
set groups nfx-sdwan-jcp-mgmnt system login class readwrite permissions view
set groups nfx-sdwan-jcp-mgmnt system login class readwrite allow-commands "(request support information)"
set groups nfx-sdwan-jcp-mgmnt system login class read-only-user-local permissions network
set groups nfx-sdwan-jcp-mgmnt system login class read-only-user-local permissions view
set groups nfx-sdwan-jcp-mgmnt system login class read-only-user-local permissions view-configuration
set groups nfx-sdwan-jcp-mgmnt system login class super-user-local permissions all
set groups nfx-sdwan-jcp-mgmnt system login user remote full-name "TACACS User"
set groups nfx-sdwan-jcp-mgmnt system login user remote class super-user-local
set groups nfx-sdwan-jcp-mgmnt system login user remote-read-only full-name "TACACS read-only user"
set groups nfx-sdwan-jcp-mgmnt system login user remote-read-only class read-only-user-local
set groups nfx-sdwan-jcp-mgmnt system login user local full-name "Local user"
set groups nfx-sdwan-jcp-mgmnt system login user local class super-user-local
set groups nfx-sdwan-jcp-mgmnt system login user local authentication plain-text-password-value {{ post_config.tacacs.local_password }}

set groups nfx-sdwan-jcp-mgmnt routing-options static route {{ post_config.tacacs.tacacs_server_ip }} next-hop {{local_loopback_address}}

set apply-groups nfx-sdwan-jcp-mgmnt
{% endif%}


{# NTP Configuration Section #}

{% if post_config.ntp.enable_service is defined and post_config.ntp.enable_service and
      post_config.ntp.NTP_servers is defined and post_config.ntp.NTP_servers %}

{% for ntp_server in post_config.ntp.NTP_servers %}
set groups nfx-sdwan-jcp-mgmnt system ntp server {{ ntp_server }}
set groups nfx-sdwan-jcp-mgmnt routing-options static route {{ ntp_server }} next-hop {{local_loopback_address}}

{% endfor %}

{% if post_config.ntp.NTP_Boot_Server is defined %}
set groups nfx-sdwan-jcp-mgmnt system ntp boot-server {{ post_config.ntp.NTP_Boot_Server }}
set groups nfx-sdwan-jcp-mgmnt routing-options static route {{ ntp_server }} next-hop {{local_loopback_address}}
{% endif %}

{% if post_config.ntp.NTP_Source_Address is defined %}
set groups nfx-sdwan-jcp-mgmnt system ntp source-address {{ post_config.ntp.NTP_Source_Address }}
{% endif %}


set apply-groups nfx-sdwan-jcp-mgmnt

{% endif %}

{# Alarms Configuration Section #}

{% if post_config.alarms.mgmt_link_ignore is defined and post_config.alarms.mgmt_link_ignore %}

set groups nfx-sdwan-jcp-mgmnt chassis alarm management-ethernet link-down ignore
set apply-groups nfx-sdwan-jcp-mgmnt

{% endif %}

