set groups nfx-sdwan-jcp-mgmnt snmp v3 usm local-engine user {{ item.snmp_user }} privacy-none
{% endif %}


set groups nfx-sdwan-jcp-mgmnt snmp v3 vacm security-to-group security-model usm security-name {{ item.snmp_user }} group grpjnpr

{% endfor %}


