set groups nfx-sdwan-jcp-mgmnt system syslog file messages any info
set groups nfx-sdwan-jcp-mgmnt system syslog file messages authorization none
set groups nfx-sdwan-jcp-mgmnt system syslog file messages firewall none
set groups nfx-sdwan-jcp-mgmnt system syslog file messages change-log notice
