set groups nfx-sdwan-jcp-mgmnt firewall family inet filter protect_re_ipv4 term tacplus then accept
set groups nfx-sdwan-jcp-mgmnt firewall family inet filter protect_re_ipv4 term tacplus1 from protocol tcp
set groups nfx-sdwan-jcp-mgmnt firewall family inet filter protect_re_ipv4 term tacplus1 from source-port {{ post_config.tacacs.tacacs_server_port }}
set groups nfx-sdwan-jcp-mgmnt firewall family inet filter protect_re_ipv4 term tacplus1 then accept

