    {% else %}
set groups nfx-sdwan-jcp-mgmnt system accounting destination tacplus server {{ post_config.tacacs.tacacs_server_ip }} port 49
    {% endif %}
set groups nfx-sdwan-jcp-mgmnt system accounting destination tacplus server {{ post_config.tacacs.tacacs_server_ip }} secret {{ post_config.tacacs.tacacs_server_password }}
set groups nfx-sdwan-jcp-mgmnt system accounting destination tacplus server {{ post_config.tacacs.tacacs_server_ip }} timeout 3

